import plotly.graph_objs as go
import numpy as np

# generowanie danych
x = np.linspace(-np.pi, np.pi, 1000)
y = np.sin(x)

# tworzenie wykresu
fig = go.Figure(data=go.Scatter(x=x, y=y, mode='lines'))

# konfiguracja osi i tytułu
fig.update_layout(
    xaxis_title='x',
    yaxis_title='sin(x)',
    title='Wykres funkcji sinus'
)

# wyświetlenie wykresu
fig.show()